import React, { useEffect, useState, useCallback } from 'react';
import { createChart } from 'lightweight-charts';
import axios from 'axios';

const ChartAndControls = ({
  chartContainerRef,
  candlestickSeries,
  setCandlestickSeries,
  currentIndex,
  setClickedPoint,
  setTotalPnL,
  setTrades,
  handleTrade,
  addNewCandle
}) => {
  const [projectionSettings, setProjectionSettings] = useState({
    timeframe: '1h',
    riskRewardRatio: 2,
    defaultStopLoss: 0.5, // percentage
  });

  const [activeProjection, setActiveProjection] = useState(null);
  const [projectionLines, setProjectionLines] = useState([]);

  const clearProjections = useCallback(() => {
    projectionLines.forEach(line => line.remove());
    setProjectionLines([]);
    setActiveProjection(null);
  }, [projectionLines]);

  useEffect(() => {
    if (!chartContainerRef.current) return;

    const chart = createChart(chartContainerRef.current, {
      width: chartContainerRef.current.clientWidth,
      height: 500,
      layout: {
        background: { color: '#1a1a1a' },
        textColor: '#d1d4dc',
      },
      grid: {
        vertLines: { color: '#2a2a2a' },
        horzLines: { color: '#2a2a2a' },
      },
      crosshair: {
        mode: 1,
        vertLine: {
          width: 1,
          color: '#758696',
          style: 3,
        },
        horzLine: {
          width: 1,
          color: '#758696',
          style: 3,
        },
      },
      timeScale: {
        timeVisible: true,
        secondsVisible: false,
        borderColor: '#2a2a2a',
      },
    });

    const series = chart.addCandlestickSeries({
      upColor: '#26a69a',
      downColor: '#ef5350',
      borderVisible: false,
      wickUpColor: '#26a69a',
      wickDownColor: '#ef5350',
    });

    setCandlestickSeries(series);

    chart.subscribeClick((param) => {
      if (!param.point || !param.time) return;

      clearProjections();

      const clickPrice = series.coordinateToPrice(param.point.y);
      const stopLoss = clickPrice * (1 - projectionSettings.defaultStopLoss / 100);
      const takeProfit = clickPrice + (clickPrice - stopLoss) * projectionSettings.riskRewardRatio;

      setClickedPoint({
        time: new Date(param.time * 1000).toLocaleString(),
        price: clickPrice
      });

      // Add projection lines
      const projectionTime = getProjectionTime(param.time, projectionSettings.timeframe);

      const tpLine = chart.addLineSeries({
        color: '#26a69a',
        lineWidth: 2,
        lineStyle: 2,
      });

      const slLine = chart.addLineSeries({
        color: '#ef5350',
        lineWidth: 2,
        lineStyle: 2,
      });

      const entryLine = chart.addLineSeries({
        color: '#758696',
        lineWidth: 2,
      });

      tpLine.setData([
        { time: param.time, value: takeProfit },
        { time: projectionTime, value: takeProfit },
      ]);

      slLine.setData([
        { time: param.time, value: stopLoss },
        { time: projectionTime, value: stopLoss },
      ]);

      entryLine.setData([
        { time: param.time, value: clickPrice },
        { time: projectionTime, value: clickPrice },
      ]);

      setProjectionLines([tpLine, slLine, entryLine]);
      setActiveProjection({
        entry: clickPrice,
        stopLoss,
        takeProfit,
        time: new Date(param.time * 1000).toLocaleString(),
      });
    });

    return () => chart.remove();
  }, [projectionSettings, clearProjections, setClickedPoint, setCandlestickSeries]);

  const getProjectionTime = (currentTime, timeframe) => {
    const multiplier = {
      '15m': 15 * 60,
      '1h': 60 * 60,
      '4h': 4 * 60 * 60,
      '1d': 24 * 60 * 60,
    }[timeframe] || 60 * 60;

    return currentTime + multiplier;
  };

  return (
    <div className="w-full border border-gray-700 rounded p-4 bg-gray-900">
      <div className="pb-4 border-b border-gray-700 mb-4">
        <h2 className="text-xl text-gray-200 flex justify-between items-center">
          <span>Trading Projection Tool</span>
          <span className="text-gray-400">⚙️</span>
        </h2>
      </div>

      <div className="space-y-4">
        <div ref={chartContainerRef} className="w-full h-[500px] border border-gray-700 rounded" />

        <div className="flex items-center gap-4 pt-4">
          <select
            value={projectionSettings.timeframe}
            onChange={(e) => setProjectionSettings(prev => ({ ...prev, timeframe: e.target.value }))}
            className="px-3 py-2 bg-gray-800 border border-gray-700 rounded text-gray-200"
          >
            <option value="15m">15m</option>
            <option value="1h">1h</option>
            <option value="4h">4h</option>
            <option value="1d">1d</option>
          </select>

          <button
            onClick={() => handleTrade('buy')}
            className="px-4 py-2 bg-green-600 hover:bg-green-700 text-white rounded flex items-center"
          >
            ↑ Long
          </button>

          <button
            onClick={() => handleTrade('sell')}
            className="px-4 py-2 bg-red-600 hover:bg-red-700 text-white rounded flex items-center"
          >
            ↓ Short
          </button>

          <button
            onClick={addNewCandle}
            className="px-4 py-2 border border-gray-600 text-gray-200 rounded hover:bg-gray-800 flex items-center"
          >
            ⟷ Next Candle
          </button>
        </div>

        {activeProjection && (
          <div className="grid grid-cols-3 gap-4 pt-4 text-sm">
            <div className="p-3 rounded bg-blue-500/10 border border-blue-500/20">
              <div className="text-blue-400">Entry</div>
              <div className="font-mono text-gray-200">{activeProjection.entry.toFixed(5)}</div>
            </div>
            <div className="p-3 rounded bg-red-500/10 border border-red-500/20">
              <div className="text-red-400">Stop Loss</div>
              <div className="font-mono text-gray-200">{activeProjection.stopLoss.toFixed(5)}</div>
            </div>
            <div className="p-3 rounded bg-green-500/10 border border-green-500/20">
              <div className="text-green-400">Take Profit</div>
              <div className="font-mono text-gray-200">{activeProjection.takeProfit.toFixed(5)}</div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default ChartAndControls;
