import { useEffect, useState } from 'react';
import axios from 'axios';
import { createChart } from 'lightweight-charts';

const ChartAndControls = ({
  chartContainerRef,
  candlestickSeries,
  setCandlestickSeries,
  currentIndex,
  setCurrentIndex,
  startDate,
  timeInterval,
  setClickedPoint,
  setTotalPnL,
  setTrades,
  handleTrade,
  addNewCandle
}) => {
  const [projectionLines, setProjectionLines] = useState([]);

  useEffect(() => {
    if (!chartContainerRef.current) return;

    const chart = createChart(chartContainerRef.current, {
      width: chartContainerRef.current.clientWidth,
      height: 400,
      layout: { backgroundColor: '#ffffff', textColor: '#333' },
      grid: { vertLines: { color: '#eee' }, horzLines: { color: '#eee' } },
      priceScale: { minMove: 0.00001 },
      timeScale: { timeVisible: true, secondsVisible: false, borderColor: '#ccc' },
    });

    const series = chart.addCandlestickSeries();
    setCandlestickSeries(series);

    axios.post('http://127.0.0.1:5000/api/historical_data', { startDate, timeInterval })
      .then((response) => {
        if (response.data.length > 0) {
          const adjustedData = response.data.map(candle => ({
            time: new Date(candle.time).getTime() / 1000,
            open: candle.open,
            high: candle.high,
            low: candle.low,
            close: candle.close,
          }));
          series.setData(adjustedData);
          setCurrentIndex(adjustedData.length);
        }
      })
      .catch((error) => console.error('Error fetching data:', error));

    chart.subscribeClick((param) => {
      if (!param || !param.time || !param.point) return;

      const price = series.coordinateToPrice(param.point.y);
      setClickedPoint({
        time: new Date(param.time * 1000).toLocaleString(),
        price
      });

      // Example: Add short/long projections based on the click point
      const longProjection = price * 1.0002;  // 2% increase for long projection
      const shortProjection = price * 0.98; // 2% decrease for short projection

      // Create projection lines data
      const newProjectionLines = [
        { time: param.time, price: longProjection, color: 'green', id: 'long' },
        { time: param.time, price: shortProjection, color: 'red', id: 'short' },
      ];

      setProjectionLines(newProjectionLines);

      // Add projection lines to the chart using separate line series
      newProjectionLines.forEach((line) => {
        const projectionSeries = chart.addLineSeries({
          color: line.color,
          lineWidth: 2,
        });

        // Project forward by 1 hour (you can adjust this)
        projectionSeries.setData([
          { time: param.time, value: price },
          { time: param.time + 60 * 60, value: line.price }, 
        ]);
      });
    });

    return () => chart.remove();
  }, [startDate, timeInterval]);

  return (
    <>
      <div ref={chartContainerRef} style={{ width: '100%', height: '400px', border: '1px solid black' }}></div>

      <div style={{ marginTop: '10px' }}>
        <button onClick={() => handleTrade('buy')} style={{ backgroundColor: 'green', color: 'white', padding: '10px' }}>
          Buy
        </button>
        <button onClick={() => handleTrade('sell')} style={{ backgroundColor: 'red', color: 'white', padding: '10px', marginLeft: '10px' }}>
          Sell
        </button>
        <button onClick={addNewCandle} style={{ backgroundColor: 'blue', color: 'white', padding: '10px', marginLeft: '10px' }}>
          Next Candle
        </button>
      </div>
    </>
  );
};

export default ChartAndControls;
