import { useEffect, useState } from 'react';
import axios from 'axios';
import { createChart } from 'lightweight-charts';

const ChartAndControls = ({
  chartContainerRef,
  setCandlestickSeries,
  currentIndex,
  setCurrentIndex,
  startDate,
  timeInterval,
  setClickedPoint,
  handleTrade,
  addNewCandle
}) => {
  const [projectionMode, setProjectionMode] = useState('long');
  const [projections, setProjections] = useState([]);
  const [chart, setChart] = useState(null);
  const [selectedProjection, setSelectedProjection] = useState(null);

  useEffect(() => {
    if (!chartContainerRef.current) return;

    const newChart = createChart(chartContainerRef.current, {
      width: chartContainerRef.current.clientWidth,
      height: 400,
      layout: { backgroundColor: '#ffffff', textColor: '#333' },
      grid: { vertLines: { color: '#eee' }, horzLines: { color: '#eee' } },
      priceScale: { minMove: 0.0001 },
      timeScale: { timeVisible: true, secondsVisible: false, borderColor: '#ccc' },
      crosshair: {
        mode: 1,
        vertLine: {
          width: 8,
          color: '#C3BCDB44',
          style: 0,
        },
        horzLine: {
          width: 8,
          color: '#C3BCDB44',
          style: 0,
        },
      },
    });

    const series = newChart.addCandlestickSeries();
    setCandlestickSeries(series);
    setChart(newChart);

    axios.post('http://127.0.0.1:5000/api/historical_data', { startDate, timeInterval })
      .then((response) => {
        if (response.data.length > 0) {
          const adjustedData = response.data.map(candle => ({
            time: new Date(candle.time).getTime() / 1000,
            open: candle.open,
            high: candle.high,
            low: candle.low,
            close: candle.close,
          }));
          series.setData(adjustedData);
          setCurrentIndex(adjustedData.length);
        }
      })
      .catch((error) => console.error('Error fetching data:', error));

    return () => newChart.remove();
  }, [chartContainerRef, setCandlestickSeries, setCurrentIndex, startDate, timeInterval]);

  useEffect(() => {
    if (!chart) return;

    const handleClick = (param) => {
      if (!param || !param.time || !param.point) return;

      const price = chart.priceScale().coordinateToPrice(param.point.y);
      setClickedPoint({ time: new Date(param.time * 1000).toLocaleString(), price });

      // Check if we clicked near an existing projection
      const clickedProjection = findNearestProjection(param.point.y, param.time);
      if (clickedProjection) {
        setSelectedProjection(clickedProjection.id);
        return;
      }

      const pipValue = 0.0001;
      const longPips = 40 * pipValue;
      const shortPips = 30 * pipValue;

      const lastCandleTime = currentIndex > 0 ? currentIndex * timeInterval : param.time;
      const projectionTimeRange = lastCandleTime + timeInterval * 5;

      let topPrice, bottomPrice, topColor, bottomColor;

      if (projectionMode === 'long') {
        topPrice = price + longPips;
        bottomPrice = price - shortPips;
        topColor = 'rgba(76, 175, 80, 0.8)';
        bottomColor = 'rgba(244, 67, 54, 0.8)';
      } else {
        topPrice = price + shortPips;
        bottomPrice = price - longPips;
        topColor = 'rgba(244, 67, 54, 0.8)';
        bottomColor = 'rgba(76, 175, 80, 0.8)';
      }

      const projectionId = Date.now();
      const projectionNumber = projections.length + 1;

      const topLine = chart.addLineSeries({
        color: topColor,
        lineWidth: 2,
        title: `Projection ${projectionNumber} Top`,
      });

      const bottomLine = chart.addLineSeries({
        color: bottomColor,
        lineWidth: 2,
        title: `Projection ${projectionNumber} Bottom`,
      });

      topLine.setData([
        { time: param.time, value: topPrice },
        { time: projectionTimeRange, value: topPrice },
      ]);

      bottomLine.setData([
        { time: param.time, value: bottomPrice },
        { time: projectionTimeRange, value: bottomPrice },
      ]);

      setProjections((prev) => [
        ...prev,
        {
          id: projectionId,
          number: projectionNumber,
          series: [topLine, bottomLine],
          prices: { top: topPrice, bottom: bottomPrice },
          time: param.time,
          mode: projectionMode,
        }
      ]);
    };

    const findNearestProjection = (y, time) => {
      const tolerance = 0.0002; // Adjust this value to change click sensitivity
      const price = chart.priceScale().coordinateToPrice(y);

      return projections.find(proj => {
        const timeMatch = Math.abs(proj.time - time) < timeInterval;
        const priceMatch = 
          Math.abs(proj.prices.top - price) < tolerance ||
          Math.abs(proj.prices.bottom - price) < tolerance;
        return timeMatch && priceMatch;
      });
    };

    chart.subscribeClick(handleClick);
    return () => chart.unsubscribeClick(handleClick);
  }, [chart, setClickedPoint, projectionMode, currentIndex, timeInterval, projections]);

  const removeProjection = (id) => {
    setProjections((prev) => {
      return prev.filter((proj) => {
        if (proj.id === id) {
          proj.series.forEach((line) => {
            if (line) chart.removeSeries(line);
          });
          return false;
        }
        return true;
      });
    });
    setSelectedProjection(null);
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column', gap: '1rem' }}>
      <div 
        ref={chartContainerRef} 
        style={{ 
          width: '100%', 
          height: '400px', 
          border: '1px solid #e2e8f0',
          borderRadius: '0.5rem'
        }}
      />

      <div style={{ display: 'flex', gap: '0.5rem' }}>
        <button
          onClick={() => setProjectionMode('long')}
          style={{
            backgroundColor: projectionMode === 'long' ? '#4CAF50' : '#e2e8f0',
            color: projectionMode === 'long' ? 'white' : '#4a5568',
            padding: '0.5rem 1rem',
            borderRadius: '0.375rem',
            border: 'none',
            cursor: 'pointer'
          }}
        >
          Long Mode
        </button>
        <button
          onClick={() => setProjectionMode('short')}
          style={{
            backgroundColor: projectionMode === 'short' ? '#f44336' : '#e2e8f0',
            color: projectionMode === 'short' ? 'white' : '#4a5568',
            padding: '0.5rem 1rem',
            borderRadius: '0.375rem',
            border: 'none',
            cursor: 'pointer'
          }}
        >
          Short Mode
        </button>
        <button
          onClick={() => handleTrade('buy')}
          style={{
            backgroundColor: '#4CAF50',
            color: 'white',
            padding: '0.5rem 1rem',
            borderRadius: '0.375rem',
            border: 'none',
            cursor: 'pointer'
          }}
        >
          Buy
        </button>
        <button
          onClick={() => handleTrade('sell')}
          style={{
            backgroundColor: '#f44336',
            color: 'white',
            padding: '0.5rem 1rem',
            borderRadius: '0.375rem',
            border: 'none',
            cursor: 'pointer'
          }}
        >
          Sell
        </button>
        <button
          onClick={addNewCandle}
          style={{
            backgroundColor: '#2196F3',
            color: 'white',
            padding: '0.5rem 1rem',
            borderRadius: '0.375rem',
            border: 'none',
            cursor: 'pointer'
          }}
        >
          Next Candle
        </button>
      </div>

      <div style={{ 
        border: '1px solid #e2e8f0', 
        borderRadius: '0.5rem', 
        padding: '1rem' 
      }}>
        <h3 style={{ 
          margin: '0 0 1rem 0',
          fontSize: '1.25rem',
          fontWeight: '600'
        }}>
          Active Projections
        </h3>
        <div style={{ 
          display: 'grid', 
          gridTemplateColumns: 'repeat(auto-fill, minmax(200px, 1fr))', 
          gap: '0.5rem' 
        }}>
          {projections.map((proj) => (
            <div
              key={proj.id}
              style={{
                padding: '0.5rem',
                borderRadius: '0.375rem',
                border: `1px solid ${selectedProjection === proj.id ? '#3b82f6' : '#e2e8f0'}`,
                backgroundColor: selectedProjection === proj.id ? '#eff6ff' : 'white',
                display: 'flex',
                justifyContent: 'space-between',
                alignItems: 'center'
              }}
            >
              <div>
                <span style={{ fontWeight: '500' }}>
                  Projection {proj.number}
                </span>
                <div style={{ 
                  fontSize: '0.875rem', 
                  color: '#666',
                  marginTop: '0.25rem' 
                }}>
                  {proj.mode.charAt(0).toUpperCase() + proj.mode.slice(1)}
                </div>
              </div>
              <button
                onClick={() => removeProjection(proj.id)}
                style={{
                  backgroundColor: '#ef4444',
                  color: 'white',
                  padding: '0.25rem 0.5rem',
                  borderRadius: '0.25rem',
                  border: 'none',
                  cursor: 'pointer',
                  fontSize: '0.875rem'
                }}
              >
                Delete
              </button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default ChartAndControls;
