import { useEffect, useState, useRef } from 'react';
import axios from 'axios';
import { createChart } from 'lightweight-charts';

const ChartAndControls = ({
  chartContainerRef,
  candlestickSeries,
  setCandlestickSeries,
  currentIndex,
  setCurrentIndex,
  startDate,
  timeInterval,
  setClickedPoint,
  setTotalPnL,
  setTrades,
  handleTrade,
  addNewCandle
}) => {
  const [projection, setProjection] = useState(null); // For storing the active projection
  const chartRef = useRef(null); // Ref to store chart instance

  useEffect(() => {
    if (!chartContainerRef.current) return;

    // Create the chart and store it in the ref
    chartRef.current = createChart(chartContainerRef.current, {
      width: chartContainerRef.current.clientWidth,
      height: 400,
      layout: { backgroundColor: '#ffffff', textColor: '#333' },
      grid: { vertLines: { color: '#eee' }, horzLines: { color: '#eee' } },
      priceScale: { minMove: 0.00001 },
      timeScale: { timeVisible: true, secondsVisible: false, borderColor: '#ccc' },
    });

    const series = chartRef.current.addCandlestickSeries();
    setCandlestickSeries(series);

    axios.post('http://127.0.0.1:5000/api/historical_data', { startDate, timeInterval })
      .then((response) => {
        if (response.data.length > 0) {
          const adjustedData = response.data.map(candle => ({
            time: new Date(candle.time).getTime() / 1000,
            open: candle.open,
            high: candle.high,
            low: candle.low,
            close: candle.close,
          }));
          series.setData(adjustedData);
          setCurrentIndex(adjustedData.length);
        }
      })
      .catch((error) => console.error('Error fetching data:', error));

    // Add click functionality to draw projections
    chartRef.current.subscribeClick((param) => {
      if (!param || !param.time || !param.point) return;
      const price = series.coordinateToPrice(param.point.y);
      setClickedPoint({
        time: new Date(param.time * 1000).toLocaleString(),
        price
      });

      // If there's already an active projection, finalize it
      if (projection) {
        drawProjection(projection.entryTime, projection.entryPrice, param.time, price);
        setProjection(null); // Reset after drawing the projection
      } else {
        // Start a new projection
        setProjection({
          entryTime: param.time,
          entryPrice: price
        });
      }
    });

    return () => chartRef.current.remove();
  }, [startDate, timeInterval, projection, setCandlestickSeries, setCurrentIndex]);

  // Function to draw the projection lines (long/short)
  const drawProjection = (entryTime, entryPrice, exitTime, exitPrice) => {
    const projectionLineSeries = chartRef.current.addLineSeries({ color: 'green', lineWidth: 2 });
    const stopLossLineSeries = chartRef.current.addLineSeries({ color: 'red', lineWidth: 1 });
    const targetLineSeries = chartRef.current.addLineSeries({ color: 'blue', lineWidth: 1 });

    // Ensure we're passing valid time and price values to the lines
    if (!entryTime || !exitTime || !entryPrice || !exitPrice) return;

    const projectionData = [
      { time: entryTime, value: entryPrice },
      { time: exitTime, value: exitPrice },
    ];

    projectionLineSeries.setData(projectionData);

    // Example: Stop-loss and target (these can be customized)
    const stopLossPrice = entryPrice * 0.95; // Stop-loss at 5% below entry price (for long)
    const targetPrice = entryPrice * 1.05; // Target at 5% above entry price (for long)

    stopLossLineSeries.setData([
      { time: entryTime, value: stopLossPrice },
      { time: exitTime, value: stopLossPrice },
    ]);

    targetLineSeries.setData([
      { time: entryTime, value: targetPrice },
      { time: exitTime, value: targetPrice },
    ]);
  };

  return (
    <>
      <div ref={chartContainerRef} style={{ width: '100%', height: '400px', border: '1px solid black' }}></div>

      <div style={{ marginTop: '10px' }}>
        <button onClick={() => handleTrade('buy')} style={{ backgroundColor: 'green', color: 'white', padding: '10px' }}>
          Buy
        </button>
        <button onClick={() => handleTrade('sell')} style={{ backgroundColor: 'red', color: 'white', padding: '10px', marginLeft: '10px' }}>
          Sell
        </button>
        <button onClick={addNewCandle} style={{ backgroundColor: 'blue', color: 'white', padding: '10px', marginLeft: '10px' }}>
          Next Candle
        </button>
      </div>
    </>
  );
};

export default ChartAndControls;
