import React, { useEffect, useState, useCallback } from 'react';
import { Card, CardHeader, CardTitle, CardContent } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { ArrowUp, ArrowDown, MoveHorizontal, Settings2 } from 'lucide-react';

const ChartAndControls = ({
  chartContainerRef,
  candlestickSeries,
  setCandlestickSeries,
  currentIndex,
  handleTrade,
  addNewCandle
}) => {
  // Rest of the code remains exactly the same as the previous version, 
  // just with ChartAndControls as the component name instead of ProjectionTool
  const [projectionSettings, setProjectionSettings] = useState({
    timeframe: '1h',
    riskRewardRatio: 2,
    defaultStopLoss: 0.5, // percentage
  });

  const [activeProjection, setActiveProjection] = useState(null);
  const [projectionLines, setProjectionLines] = useState([]);

  const clearProjections = useCallback(() => {
    projectionLines.forEach(line => line.remove());
    setProjectionLines([]);
    setActiveProjection(null);
  }, [projectionLines]);

  useEffect(() => {
    if (!chartContainerRef.current) return;

    const chart = createChart(chartContainerRef.current, {
      width: chartContainerRef.current.clientWidth,
      height: 500,
      layout: {
        background: { color: '#1a1a1a' },
        textColor: '#d1d4dc',
      },
      grid: {
        vertLines: { color: '#2a2a2a' },
        horzLines: { color: '#2a2a2a' },
      },
      crosshair: {
        mode: 1,
        vertLine: {
          width: 1,
          color: '#758696',
          style: 3,
        },
        horzLine: {
          width: 1,
          color: '#758696',
          style: 3,
        },
      },
      timeScale: {
        timeVisible: true,
        secondsVisible: false,
        borderColor: '#2a2a2a',
      },
    });

    const series = chart.addCandlestickSeries({
      upColor: '#26a69a',
      downColor: '#ef5350',
      borderVisible: false,
      wickUpColor: '#26a69a',
      wickDownColor: '#ef5350',
    });

    setCandlestickSeries(series);

    chart.subscribeClick((param) => {
      if (!param.point || !param.time) return;

      clearProjections();

      const clickPrice = series.coordinateToPrice(param.point.y);
      const stopLoss = clickPrice * (1 - projectionSettings.defaultStopLoss / 100);
      const takeProfit = clickPrice + (clickPrice - stopLoss) * projectionSettings.riskRewardRatio;

      // Add projection lines
      const projectionTime = getProjectionTime(param.time, projectionSettings.timeframe);

      const tpLine = chart.addLineSeries({
        color: '#26a69a',
        lineWidth: 2,
        lineStyle: 2,
      });

      const slLine = chart.addLineSeries({
        color: '#ef5350',
        lineWidth: 2,
        lineStyle: 2,
      });

      const entryLine = chart.addLineSeries({
        color: '#758696',
        lineWidth: 2,
      });

      tpLine.setData([
        { time: param.time, value: takeProfit },
        { time: projectionTime, value: takeProfit },
      ]);

      slLine.setData([
        { time: param.time, value: stopLoss },
        { time: projectionTime, value: stopLoss },
      ]);

      entryLine.setData([
        { time: param.time, value: clickPrice },
        { time: projectionTime, value: clickPrice },
      ]);

      setProjectionLines([tpLine, slLine, entryLine]);
      setActiveProjection({
        entry: clickPrice,
        stopLoss,
        takeProfit,
        time: new Date(param.time * 1000).toLocaleString(),
      });
    });

    return () => chart.remove();
  }, [projectionSettings]);

  const getProjectionTime = (currentTime, timeframe) => {
    const multiplier = {
      '15m': 15 * 60,
      '1h': 60 * 60,
      '4h': 4 * 60 * 60,
      '1d': 24 * 60 * 60,
    }[timeframe] || 60 * 60;

    return currentTime + multiplier;
  };

  return (
    <Card className="w-full">
      <CardHeader className="pb-4">
        <CardTitle className="flex items-center justify-between">
          <span>Trading Projection Tool</span>
          <Settings2 className="w-5 h-5" />
        </CardTitle>
      </CardHeader>
      <CardContent>
        <div className="space-y-4">
          <div ref={chartContainerRef} className="w-full h-[500px] border border-gray-700 rounded-lg" />

          <div className="flex items-center gap-4 pt-4">
            <Select
              value={projectionSettings.timeframe}
              onValueChange={(value) => setProjectionSettings(prev => ({ ...prev, timeframe: value }))}
            >
              <SelectTrigger className="w-24">
                <SelectValue placeholder="Timeframe" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="15m">15m</SelectItem>
                <SelectItem value="1h">1h</SelectItem>
                <SelectItem value="4h">4h</SelectItem>
                <SelectItem value="1d">1d</SelectItem>
              </SelectContent>
            </Select>

            <Button onClick={() => handleTrade('buy')} className="bg-green-600 hover:bg-green-700">
              <ArrowUp className="w-4 h-4 mr-2" />
              Long
            </Button>

            <Button onClick={() => handleTrade('sell')} className="bg-red-600 hover:bg-red-700">
              <ArrowDown className="w-4 h-4 mr-2" />
              Short
            </Button>

            <Button onClick={addNewCandle} variant="outline">
              <MoveHorizontal className="w-4 h-4 mr-2" />
              Next Candle
            </Button>
          </div>

          {activeProjection && (
            <div className="grid grid-cols-3 gap-4 pt-4 text-sm">
              <div className="p-3 rounded bg-blue-500/10 border border-blue-500/20">
                <div className="text-blue-400">Entry</div>
                <div className="font-mono">{activeProjection.entry.toFixed(5)}</div>
              </div>
              <div className="p-3 rounded bg-red-500/10 border border-red-500/20">
                <div className="text-red-400">Stop Loss</div>
                <div className="font-mono">{activeProjection.stopLoss.toFixed(5)}</div>
              </div>
              <div className="p-3 rounded bg-green-500/10 border border-green-500/20">
                <div className="text-green-400">Take Profit</div>
                <div className="font-mono">{activeProjection.takeProfit.toFixed(5)}</div>
              </div>
            </div>
          )}
        </div>
      </CardContent>
    </Card>
  );
};

export default ChartAndControls;
