import axios from 'axios'; // Removed 'useEffect'
import { useState, useRef } from 'react'; // Removed 'useEffect'
import { createChart } from 'lightweight-charts'; // Remove if not needed

const ChartAndControls = ({
  chartContainerRef,
  candlestickSeries,
  setCandlestickSeries,
  currentIndex,
  setCurrentIndex,
  startDate,
  timeInterval,
  setClickedPoint,
  handleTrade,
  addNewCandle
}) => {
  const chartRef = useRef(null);
  const seriesRef = useRef(null);
  const [projectionMode, setProjectionMode] = useState(null);
  const [activeProjection, setActiveProjection] = useState(null);
  const [isDragging, setIsDragging] = useState(false);

  // If 'trades' is not needed, remove it from props
  // If it's needed, use it somewhere in your component

  return (
    <>
      <div ref={chartContainerRef} style={{ width: '100%', height: '400px', border: '1px solid black' }}></div>

      <div style={{ marginTop: '10px', position: 'relative', zIndex: 10 }}>
        <button
          onClick={() => setProjectionMode('long')}
          style={{ backgroundColor: 'green', color: 'white', padding: '10px', cursor: 'pointer' }}
        >
          Long Projection
        </button>
        <button
          onClick={() => setProjectionMode('short')}
          style={{ backgroundColor: 'red', color: 'white', padding: '10px', marginLeft: '10px', cursor: 'pointer' }}
        >
          Short Projection
        </button>
        <button
          onClick={addNewCandle}
          style={{ backgroundColor: 'blue', color: 'white', padding: '10px', marginLeft: '10px', cursor: 'pointer' }}
        >
          Next Candle
        </button>
      </div>
    </>
  );
};

export default ChartAndControls;
