import React, { useState } from 'react';

const Form = () => {
  const [trade, setTrade] = useState({
    action: '',
    price: '',
    quantity: ''
  });

  const handleInputChange = (key, value) => {
    setTrade({ ...trade, [key]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!trade.action || !trade.price || !trade.quantity) {
      alert('All fields are required!');
      return;
    }

    try {
      const response = await fetch('http://127.0.0.1:5000/api/account', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          ...trade,
          time: new Date().toISOString().split('T')[0]
        }),
      });

      const data = await response.json();

      if (response.ok) {
        alert('Trade placed successfully!');
        setTrade({ action: '', price: '', quantity: '' });
      } else {
        throw new Error(data.error || 'Failed to place trade');
      }
    } catch (error) {
      console.error('Error saving trade:', error);
      alert('Failed to save trade. Please try again.');
    }
  };

  return (
    <div style={styles.formContainer}>
      <h2 style={styles.heading}>Place Trade</h2>

      <form onSubmit={handleSubmit} style={styles.tradeForm}>
        <div style={styles.formGroup}>
          <label>Action</label>
          <select
            value={trade.action}
            onChange={(e) => handleInputChange('action', e.target.value)}
            style={styles.input}
          >
            <option value="">Select Action</option>
            <option value="buy">Buy</option>
            <option value="sell">Sell</option>
          </select>
        </div>

        <div style={styles.formGroup}>
          <label>Price</label>
          <input
            type="number"
            step="0.0001"
            value={trade.price}
            onChange={(e) => handleInputChange('price', e.target.value)}
            placeholder="Enter Price"
            style={styles.input}
          />
        </div>

        <div style={styles.formGroup}>
          <label>Quantity</label>
          <input
            type="number"
            step="0.01"
            value={trade.quantity}
            onChange={(e) => handleInputChange('quantity', e.target.value)}
            placeholder="Enter Quantity"
            style={styles.input}
          />
        </div>

        <button type="submit" style={styles.submitButton}>
          Place Trade
        </button>
      </form>

      {trade.action && trade.price && trade.quantity && (
        <div style={styles.tradePreview}>
          <h3>Trade Preview:</h3>
          <p><strong>Action:</strong> {trade.action.toUpperCase()}</p>
          <p><strong>Price:</strong> ${Number(trade.price).toFixed(4)}</p>
          <p><strong>Quantity:</strong> {trade.quantity}</p>
          <p><strong>Total Value:</strong> ${(Number(trade.price) * Number(trade.quantity)).toFixed(2)}</p>
        </div>
      )}
    </div>
  );
};

// Inline Styles
const styles = {
  formContainer: {
    width: '100%',
    maxWidth: '400px',
    background: '#f9f9f9',
    padding: '20px',
    borderRadius: '10px',
    boxShadow: '0px 4px 10px rgba(0, 0, 0, 0.1)',
    margin: '20px auto',
    textAlign: 'center',
  },
  heading: {
    marginBottom: '15px',
    color: '#333',
  },
  tradeForm: {
    display: 'flex',
    flexDirection: 'column',
    gap: '10px',
  },
  formGroup: {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'flex-start',
  },
  input: {
    width: '100%',
    padding: '10px',
    border: '1px solid #ccc',
    borderRadius: '5px',
    fontSize: '16px',
    transition: '0.3s ease',
  },
  submitButton: {
    background: '#007bff',
    color: 'white',
    padding: '10px',
    border: 'none',
    borderRadius: '5px',
    fontSize: '16px',
    cursor: 'pointer',
    transition: '0.3s ease',
  },
  tradePreview: {
    marginTop: '15px',
    padding: '15px',
    background: '#eaf7e9',
    borderRadius: '5px',
    textAlign: 'left',
    borderLeft: '5px solid #28a745',
  },
};

export default Form;
