import React, { useState, useEffect } from 'react';

const Form = ({ updateTrades }) => {
  const [trade, setTrade] = useState({
    action: '',
    price: '',
    quantity: '',
    stopLoss: '',
    takeProfit: '',
    condition: '', // For conditional trades
  });

  const handleInputChange = (key, value) => {
    setTrade({ ...trade, [key]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!trade.action || !trade.price || !trade.quantity) {
      alert('All fields are required!');
      return;
    }

    try {
      const response = await fetch('http://127.0.0.1:5000/api/account', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          ...trade,
          time: new Date().toISOString().split('T')[0],
        }),
      });

      const data = await response.json();

      if (response.ok) {
        alert('Trade placed successfully!');
        updateTrades(data.trades); // Update trade list
        setTrade({ action: '', price: '', quantity: '', stopLoss: '', takeProfit: '', condition: '' });
      } else {
        throw new Error(data.error || 'Failed to place trade');
      }
    } catch (error) {
      console.error('Error saving trade:', error);
      alert('Failed to save trade. Please try again.');
    }
  };

  return (
    <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
      <select value={trade.action} onChange={(e) => handleInputChange('action', e.target.value)}>
        <option value="">Select Action</option>
        <option value="buy">Buy</option>
        <option value="sell">Sell</option>
      </select>

      <input type="number" placeholder="Price" value={trade.price} onChange={(e) => handleInputChange('price', e.target.value)} />
      <input type="number" placeholder="Quantity" value={trade.quantity} onChange={(e) => handleInputChange('quantity', e.target.value)} />

      <input type="number" placeholder="Stop Loss" value={trade.stopLoss} onChange={(e) => handleInputChange('stopLoss', e.target.value)} />
      <input type="number" placeholder="Take Profit" value={trade.takeProfit} onChange={(e) => handleInputChange('takeProfit', e.target.value)} />

      <input type="number" placeholder="Condition (Pending Order Price)" value={trade.condition} onChange={(e) => handleInputChange('condition', e.target.value)} />

      <button type="submit">Place Trade</button>
    </form>
  );
};

export default Form;
