import { useEffect, useState } from 'react';
import axios from 'axios';
import { createChart, LineStyle } from 'lightweight-charts';

const ChartAndControls = ({
  chartContainerRef,
  candlestickSeries,
  setCandlestickSeries,
  currentIndex,
  setCurrentIndex,
  startDate,
  timeInterval,
  setClickedPoint,
  setTotalPnL,
  setTrades,
  handleTrade,
  addNewCandle
}) => {
  const [projectionLines, setProjectionLines] = useState([]);
  const [projectionMode, setProjectionMode] = useState(null); // 'long' or 'short'
  const [projectionPercent, setProjectionPercent] = useState(2); // default 2%
  const [projectionHours, setProjectionHours] = useState(24); // default 24 hours

  useEffect(() => {
    if (!chartContainerRef.current) return;

    const chart = createChart(chartContainerRef.current, {
      width: chartContainerRef.current.clientWidth,
      height: 400,
      layout: { backgroundColor: '#ffffff', textColor: '#333' },
      grid: { vertLines: { color: '#eee' }, horzLines: { color: '#eee' } },
      priceScale: { minMove: 0.00001 },
      timeScale: { timeVisible: true, secondsVisible: false, borderColor: '#ccc' },
    });

    const series = chart.addCandlestickSeries();
    setCandlestickSeries(series);

    axios.post('http://127.0.0.1:5000/api/historical_data', { startDate, timeInterval })
      .then((response) => {
        if (response.data.length > 0) {
          const adjustedData = response.data.map(candle => ({
            time: new Date(candle.time).getTime() / 1000,
            open: candle.open,
            high: candle.high,
            low: candle.low,
            close: candle.close,
          }));
          series.setData(adjustedData);
          setCurrentIndex(adjustedData.length);
        }
      })
      .catch((error) => console.error('Error fetching data:', error));

    chart.subscribeClick((param) => {
      if (!param || !param.time || !param.point) return;
      const price = series.coordinateToPrice(param.point.y);
      setClickedPoint({
        time: new Date(param.time * 1000).toLocaleString(),
        price
      });

      if (projectionMode) {
        addProjectionLine(param.time, price);
      }
    });

    return () => chart.remove();
  }, [startDate, timeInterval]);

  const addProjectionLine = (clickTime, startPrice) => {
    const multiplier = projectionMode === 'long' ? (1 + projectionPercent / 100) : (1 - projectionPercent / 100);
    const endPrice = startPrice * multiplier;
    const endTime = clickTime + (projectionHours * 3600); // Convert hours to seconds

    const newLine = chartRef.current.addLineSeries({
      color: projectionMode === 'long' ? '#26a69a' : '#ef5350',
      lineWidth: 2,
      lineStyle: LineStyle.Dashed,
      lastPriceAnimation: 1,
    });

    newLine.setData([
      { time: clickTime, value: startPrice },
      { time: endTime, value: endPrice }
    ]);

    const newProjection = {
      id: Date.now(),
      series: newLine,
      mode: projectionMode,
      startPrice,
      endPrice,
      startTime: clickTime,
      endTime
    };

    setProjectionLines(prev => [...prev, newProjection]);
    setProjectionMode(null); // Reset projection mode after drawing
  };

  const clearProjections = () => {
    projectionLines.forEach(line => {
      if (chartRef.current) {
        chartRef.current.removeSeries(line.series);
      }
    });
    setProjectionLines([]); // Clear the projections
  };

  const handleProjectionPercentChange = (e) => {
    const value = parseFloat(e.target.value);
    if (!isNaN(value) && value > 0) {
      setProjectionPercent(value);
    }
  };

  const handleProjectionHoursChange = (e) => {
    const value = parseInt(e.target.value);
    if (!isNaN(value) && value > 0) {
      setProjectionHours(value);
    }
  };

  return (
    <div className="flex flex-col gap-4">
      <div ref={chartContainerRef} className="w-full h-[400px] border border-gray-300 rounded-lg"></div>

      <div className="flex flex-wrap gap-4 items-center">
        <div className="flex gap-2">
          <button 
            onClick={() => handleTrade('buy')} 
            className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600"
          >
            Buy
          </button>
          <button 
            onClick={() => handleTrade('sell')} 
            className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600"
          >
            Sell
          </button>
          <button 
            onClick={addNewCandle} 
            className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
          >
            Next Candle
          </button>
        </div>

        <div className="flex gap-2 items-center border-l pl-4">
          <button 
            onClick={() => setProjectionMode('long')}
            className={`px-4 py-2 rounded ${
              projectionMode === 'long' 
                ? 'bg-green-600 text-white' 
                : 'bg-green-100 text-green-800'
            } hover:bg-green-500 hover:text-white`}
          >
            Project Long
          </button>
          <button 
            onClick={() => setProjectionMode('short')}
            className={`px-4 py-2 rounded ${
              projectionMode === 'short' 
                ? 'bg-red-600 text-white' 
                : 'bg-red-100 text-red-800'
            } hover:bg-red-500 hover:text-white`}
          >
            Project Short
          </button>
          <button 
            onClick={clearProjections}
            className="bg-gray-500 text-white px-4 py-2 rounded hover:bg-gray-600"
          >
            Clear All
          </button>
        </div>

        <div className="flex gap-4 items-center border-l pl-4">
          <div className="flex items-center gap-2">
            <label className="text-sm">Projection %:</label>
            <input
              type="number"
              value={projectionPercent}
              onChange={handleProjectionPercentChange}
              className="w-16 px-2 py-1 border rounded"
              min="0.1"
              step="0.1"
            />
          </div>
          <div className="flex items-center gap-2">
            <label className="text-sm">Hours:</label>
            <input
              type="number"
              value={projectionHours}
              onChange={handleProjectionHoursChange}
              className="w-16 px-2 py-1 border rounded"
              min="1"
              step="1"
            />
          </div>
        </div>
      </div>
    </div>
  );
};

export default ChartAndControls;
