import { createChart } from 'lightweight-charts';
import { useEffect, useRef } from 'react';
import axios from 'axios';

const ChartContainer = ({
  chartContainerRef,
  candlestickSeries,
  currentIndex,
  setCandlestickSeries,
  setCurrentIndex,
  startDate,
  timeInterval,
  setClickedPoint,
  setTotalPnL,
  setTrades,
}) => {
  useEffect(() => {
    if (!chartContainerRef.current) return;

    const chart = createChart(chartContainerRef.current, { /* chart settings */ });
    const series = chart.addCandlestickSeries();
    setCandlestickSeries(series);

    axios.post('http://127.0.0.1:5000/api/historical_data', { startDate, timeInterval })
      .then((response) => {
        if (response.data.length > 0) {
          const adjustedData = response.data.map(candle => ({ /* data transformation */ }));
          series.setData(adjustedData);
          setCurrentIndex(adjustedData.length);
        }
      })
      .catch((error) => console.error('Error fetching data:', error));

    chart.subscribeClick((param) => {
      if (!param || !param.time || !param.point) return;
      const price = series.coordinateToPrice(param.point.y);
      setClickedPoint({ time: new Date(param.time * 1000).toLocaleString(), price });
    });

    return () => chart.remove();
  }, [startDate, timeInterval]);

  return <div ref={chartContainerRef} style={{ width: '100%', height: '400px' }}></div>;
};

export default ChartContainer;
