import { useEffect, useState, useRef, useCallback } from 'react';
import axios from 'axios';
import { createChart } from 'lightweight-charts';

const ChartAndControls = ({
  chartContainerRef,
  candlestickSeries,
  setCandlestickSeries,
  currentIndex,
  setCurrentIndex,
  startDate,
  timeInterval,
  setClickedPoint,
  handleTrade,
  addNewCandle
}) => {
  const chartRef = useRef(null);
  const seriesRef = useRef(null);
  const [projectionMode, setProjectionMode] = useState(null);
  const [activeProjection, setActiveProjection] = useState(null);
  const [isDragging, setIsDragging] = useState(false);

  // Function to start the projection
  const startProjection = useCallback((direction, entryPrice, entryTime) => {
    if (!chartRef.current) return;

    // Clear previous projection
    if (activeProjection) {
      activeProjection.projectionSeries.priceScale().applyOptions({ autoScale: true });
    }

    const color = direction === 'long' ? 'green' : 'red';

    const projectionSeries = chartRef.current.addLineSeries({ color, lineWidth: 2, lineStyle: 2 });
    const stopLossSeries = chartRef.current.addLineSeries({ color: 'red', lineWidth: 1, lineStyle: 1 });
    const targetSeries = chartRef.current.addLineSeries({ color: 'green', lineWidth: 1, lineStyle: 1 });

    setActiveProjection({
      direction,
      entryPrice,
      entryTime,
      projectionSeries,
      stopLossSeries,
      targetSeries,
    });
  }, [activeProjection]);

  // Function to update the projection while dragging
  const updateProjection = useCallback((newPrice) => {
    if (!activeProjection) return;

    const { direction, entryPrice, entryTime, projectionSeries, stopLossSeries, targetSeries } = activeProjection;

    const stopLossPrice = direction === 'long' ? Math.min(entryPrice, newPrice) : Math.max(entryPrice, newPrice);
    const targetPrice = direction === 'long' ? Math.max(entryPrice, newPrice) : Math.min(entryPrice, newPrice);

    projectionSeries.setData([
      { time: entryTime, value: entryPrice },
      { time: entryTime + 60 * 60, value: newPrice },
    ]);

    stopLossSeries.setData([
      { time: entryTime, value: stopLossPrice },
      { time: entryTime + 60 * 60, value: stopLossPrice },
    ]);

    targetSeries.setData([
      { time: entryTime, value: targetPrice },
      { time: entryTime + 60 * 60, value: targetPrice },
    ]);
  }, [activeProjection]);

  useEffect(() => {
    if (!chartContainerRef.current || chartRef.current) return;

    // Initialize chart
    chartRef.current = createChart(chartContainerRef.current, {
      width: chartContainerRef.current.clientWidth,
      height: 400,
      layout: { backgroundColor: '#ffffff', textColor: '#333' },
      grid: { vertLines: { color: '#eee' }, horzLines: { color: '#eee' } },
      priceScale: { minMove: 0.00001 },
      timeScale: { timeVisible: true, secondsVisible: false, borderColor: '#ccc' },
    });

    seriesRef.current = chartRef.current.addCandlestickSeries();
    setCandlestickSeries(seriesRef.current);

    axios.post('http://127.0.0.1:5000/api/historical_data', { startDate, timeInterval })
      .then((response) => {
        if (response.data.length > 0) {
          const adjustedData = response.data.map(candle => ({
            time: new Date(candle.time).getTime() / 1000,
            open: candle.open,
            high: candle.high,
            low: candle.low,
            close: candle.close,
          }));
          seriesRef.current.setData(adjustedData);
          setCurrentIndex(adjustedData.length);
        }
      })
      .catch((error) => console.error('Error fetching data:', error));

    chartRef.current.subscribeClick((param) => {
      if (!param || !param.time || !param.point) return;

      const price = seriesRef.current.coordinateToPrice(param.point.y);
      if (!price) return;

      if (projectionMode) {
        if (!isDragging) {
          // First click starts the projection
          startProjection(projectionMode, price, param.time);
          setIsDragging(true);
        } else {
          // Second click finalizes the projection
          setProjectionMode(null);
          setIsDragging(false);
        }
      }
    });

    chartRef.current.subscribeCrosshairMove((param) => {
      if (!isDragging || !activeProjection || !param || !param.point) return;

      const price = seriesRef.current.coordinateToPrice(param.point.y);
      if (!price) return;

      updateProjection(price);
    });

    return () => {
      chartRef.current.remove();
      chartRef.current = null;
    };
  }, [
    startDate, 
    timeInterval, 
    chartContainerRef, 
    setCandlestickSeries, 
    setCurrentIndex, 
    projectionMode, 
    isDragging, 
    activeProjection, 
    startProjection, 
    updateProjection
  ]);

  return (
    <>
      <div ref={chartContainerRef} style={{ width: '100%', height: '400px', border: '1px solid black', position: 'relative', zIndex: 0 }}></div>

      <div style={{ marginTop: '10px', position: 'relative', zIndex: 10 }}>
        <button
          onClick={() => {
            console.log('Long Projection Clicked'); 
            setProjectionMode('long');
          }}
          style={{ backgroundColor: 'green', color: 'white', padding: '10px', cursor: 'pointer' }}
        >
          Long Projection
        </button>
        <button
          onClick={() => {
            console.log('Short Projection Clicked'); 
            setProjectionMode('short');
          }}
          style={{ backgroundColor: 'red', color: 'white', padding: '10px', marginLeft: '10px', cursor: 'pointer' }}
        >
          Short Projection
        </button>
        <button
          onClick={() => {
            console.log('Next Candle Clicked'); 
            addNewCandle();
          }}
          style={{ backgroundColor: 'blue', color: 'white', padding: '10px', marginLeft: '10px', cursor: 'pointer' }}
        >
          Next Candle
        </button>
      </div>
    </>
  );
};

export default ChartAndControls;
